INSERT INTO public.products (id, kind, title, title_ar, subtitle, subtitle_ar, description, description_ar, photo, base_price_baisa, planned_dates, photos, longitude, latitude, last_updated, is_deleted) VALUES ('experience-the-culture-test', 'trip', 'Experience The Culture test', 'تجربة الثقافة 2 (test)', 'Sur Port City tour- 7 hours', 'عاصمة الثقافة الإسلامية رحلة 7 ساعات', '<p class="ql-align-center">A trip to the capital of culture and civilization,&nbsp;(<a href="https://en.wikipedia.org/wiki/Nizwa" rel="noopener noreferrer" target="_blank"><strong>Nizwa</strong></a>), including exploring, neighbouring villages, and</p><p class="ql-align-center">traditional markets. Followed by visiting the<a href="https://en.wikipedia.org/wiki/Ghul,_Oman#:~:text=Ghul%20or%20Wadi%20Ghul%20is,highest%20mountain%20peak%20in%20Oman." rel="noopener noreferrer" target="_blank"><strong>&nbsp;(grand canyon</strong></a>)&nbsp;with an experience of (<a href="https://www.lonelyplanet.com/oman/al-hamra/attractions/bait-al-safah/a/poi-sig/1367923/1331779" rel="noopener noreferrer" target="_blank"><strong>Omani home life</strong></a>). Ending by visiting the&nbsp;(<a href="https://en.wikipedia.org/wiki/Bahla_Fort" rel="noopener noreferrer" target="_blank"><strong>oldest castle</strong></a>)&nbsp;in Oman, a pottery and copper factory.</p><p class="ql-align-center"><br></p><p class="ql-align-center"><br></p>', '<p class="ql-align-right">رحلة إلى عاصمة الثقافة والحضارة ،(<a href="https://ar.wikipedia.org/wiki/%D9%88%D9%84%D8%A7%D9%8A%D8%A9_%D9%86%D8%B2%D9%88%D9%89" rel="noopener noreferrer" target="_blank"><strong>&nbsp;نزوى</strong></a>)&nbsp;، تشمل استكشاف القرى المجاورة ، والسوق التقليدي. تليها زيارة&nbsp;(<a href="https://ar.wikipedia.org/wiki/%D8%AC%D8%A8%D9%84_%D8%B4%D9%85%D8%B3" rel="noopener noreferrer" target="_blank">الوادي العظيم</a>)&nbsp;مع تجربة الحياة&nbsp;(<a href="https://ar.wikipedia.org/wiki/%D8%A8%D9%8A%D8%AA_%D8%A7%D9%84%D8%B5%D9%81%D8%A7%D8%A9" rel="noopener noreferrer" target="_blank"><strong>المنزلية العمانية</strong></a>.) وتنتهي بزيارة أقدم&nbsp;<a href="https://ar.wikipedia.org/wiki/%D9%82%D9%84%D8%B9%D8%A9_%D8%A8%D9%87%D9%84%D8%A7%D8%A1" rel="noopener noreferrer" target="_blank"><strong>قلعة</strong></a><strong>&nbsp;</strong>في عمان وهي مصنع للفخار والنحاس</p><p class="ql-align-right"><br></p>', 'https://i.imgur.com/6hMvqpN.jpg', 10000, '{}', '{}', 59.5066, 22.5653, '2023-01-02', true);
INSERT INTO public.products (id, kind, title, title_ar, subtitle, subtitle_ar, description, description_ar, photo, base_price_baisa, planned_dates, photos, longitude, latitude, last_updated, is_deleted) VALUES ('wadi-hawer', 'adventure', 'Wadi Hawer', 'وادي حاور', 'Full Day Adevnture', 'مغامرة ليوم كامل', '<p>An adventure in Oman. In this adventure, you have to jump, swim, climb and abseil.</p><p><strong>Track Length</strong>: 6 km__<strong>Duration</strong>: 4- 6 hours</p><p><strong>Number of Drops</strong>: 1__<strong>Drop Length</strong>: 10 m__</p><p><strong>Leave it on us</strong>:</p><ol><li>Transportation</li><li>Light snacks</li><li>Lunch meal</li><li>Safety equipment</li></ol><p><strong>What to bring</strong>:</p><ol><li>Backpack</li><li>Sun-protected cap</li><li>Suitable shoes</li><li>Your kind smiles</li></ol>', '<p class="ql-align-right">تجربة مغامرة في عُمان. المغامرة ستكون مزيج من القفز، السباحة، التسلق ونزول بالحبال .</p><p class="ql-align-right"><strong>طول المسار</strong>: 6 كم__<strong>المدة</strong>: 4 - 6 ساعات</p><p class="ql-align-right"><strong>عدد النزلات</strong>: 1__<strong>طول النزلة</strong>: 10 أمتار</p><p class="ql-align-right"><strong>خليها علينا</strong>:</p><p class="ql-align-right">1-النقل</p><p class="ql-align-right">2-الوجبات الخفيفه</p><p class="ql-align-right">3-وجبة الغدا</p><p class="ql-align-right">4-عدة السلامه</p><p class="ql-align-right"><strong>عليكم:</strong></p><p class="ql-align-right">1-ابتسامتك الطبيه</p><p class="ql-align-right">2-شنطة ظهر خاصه</p><p class="ql-align-right">3-كاب وقاية من الشمس</p><p class="ql-align-right">4-حذاء مناسب</p>', 'https://i.imgur.com/6hMvqpN.jpg', 55000, '{}', '{}', 59.118, 22.5458, '2023-01-03', false);
INSERT INTO public.products (id, kind, title, title_ar, subtitle, subtitle_ar, description, description_ar, photo, base_price_baisa, planned_dates, photos, longitude, latitude, last_updated, is_deleted) VALUES ('jabal-shams', 'adventure', 'Jabal Shams', 'جبل شمس', 'Full Day Adventure', 'مغامرة ليوم كامل', '<p>Going from Muscat to the highest point in the Gulf countries, feast your eyes on the Omani Grand Canyon and then pass through the ruins of old villages and even a Persian Castle. We will then descend to the oldest castle in Oman and end our trip by visiting the pottery factory.&nbsp;</p><p class="ql-align-center"><strong>Leave it to us:</strong></p><ol><li>Transportation___2. Light snacks___3. Lunch___4. Drinking water___5. Special Tour guide</li></ol><p class="ql-align-center"><strong>What to bring:</strong></p><ol><li>Suitable shoes___2. Sun-protective cap___3. Excitement</li></ol>', '<p class="ql-align-right">ننطلق من مسقط الى أعلى قمة في دول الخليج. متع عينك على مناظر الجراند كانيون العمانية تليها المرور على أنقاض القرى القديمة و القلعة الفارسية. سننزل بعدها الى أقدم قلعة في عمان ونختم رحلتنا بزياره مصنع الفخار.</p><p class="ql-align-center"><strong>خليها علينا:</strong></p><ol><li class="ql-align-right">النقل__2. وجبات خفيفة__3. وجبة الغداء__4. مياه شرب__5. مرشد سياحي خاص</li></ol><p class="ql-align-center"><strong>عليكم:</strong></p><ol><li class="ql-align-right">حذاء مناسب__2. كاب واقي من الشمس__3. حماسكم</li></ol>', 'https://www.outlookindia.com/outlooktraveller/public/uploads/articles/explore/Jebel-shams.gif', 97000, '{}', '{}', 57.2649, 23.2376, '2023-01-03', true);
